INSERT INTO public."Entertainments"("Id", "Price", "TypeEntertainment", "Details", "Area", "Latitude", "Longitude", "CreationDate", "City", "UrlImage", "Name", "UrlSite")
VALUES  ('gen_random_uuid()', 350, 0, '26 апреля 2014 года в крупнейшем в Томске торгово-развлекательном центре — «Изумрудный город» открылся новый многозальный кинотеатр GoodwinCinema. Впервые в Томске появился суперсовременный кинотеатр, сочетающий в себе все самые современные мировые технологии кинопоказа, включая инновационную технологию EmotionHalls. GoodwinCinema — это 7 уникальных кинозалов, каждый из которых предназначен для просмотра фильмов определенного жанра , создавая настроение зрителю, как только он попадает внутрь кинозала.', 1, 56.4888162848742, 84.9780045274185, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/goodwin/', 'Goodwin cinema', 'https://goodwincinema.ru/'),
        ('gen_random_uuid()', 300, 0, 'Киноцентр "Киномакс" начал свою работу 21 декабря 2006 года, и на протяжении многих лет остается одним из любимых мест отдыха жителей и гостей нашего региона. Киноцентр "Киномакс" - это 6 кинозалов, 3 бара, а также 2 двухуровневые охраняемые парковки. Кроме того, для удобства посетителей работает гардероб. ', 2, 56.5015277376617, 84.951564267898, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/kinomax/', 'Киномакс', 'https://kinomax.tomsk.ru/'),
        ('gen_random_uuid()', 330, 0, 'Современный, полностью цифровой кинотеатр "Кинополис" расположен в городе Томске, в  ТРЦ "Мегаполис". ТРЦ "Мегаполис" - это обновленный, торгово-развлекательный комплекс, в котором Вам будет не только комфортно совершать покупки, но и приятно проводить время со своей семьей и друзьями. Концепция кинотеатра подчеркивает основную идею ТРЦ: современный, комфортабельный, доступный.', 2, 56.5142259721645, 84.9435570717706, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/kinopolis/', 'Кинополис', 'https://kino-polis.ru'),
        ('gen_random_uuid()', 200, 0, 'Мультиплекс предусматривает один зал DolbyDigital с вместительностью 218 посадочных мест. Каждый зал оснащен современным оборудованием, благодаря которому демонстрируются фильмы в 3D-формате. Помимо зрительного зала в кинотеатре есть кафе-бар, боулинг, бильярд. Территория здания предусматривает бесплатную автомобильную парковку. Купить попкорн, холодные или горячие напитки можно в кино-баре, он расположен в холле киноцентра. Развлекательный комплекс «Fакел» является отличным местом для семейного отдыха, встреч с друзьями, а также для влюбленных пар. Основной контингент кинотеатра - дети и молодежь.', 3, 56.46222426007607, 84.96940701719666, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/Fakel/', 'Fakel', 'https://rkfakel.ru/'),
