INSERT INTO public."Entertainments"("Id", "Price", "TypeEntertainment", "Details", "Area", "Latitude", "Longitude", "CreationDate", "City", "UrlImage", "Name", "UrlSite")
VALUES ('gen_random_uuid()', "0", 4, 'Памятник архитектуры. Культурный центр ТПУ. Середина XIX века', 3, 56.463152568251154, 84.96021599999995, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/AttractionTpu/', 'Международный культурный центр ТПУ', ''),
('gen_random_uuid()', "0", 4, '', 0, 56.477373068257634, 84.9913874999999, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/shasSpoyu/', 'Памятник счастью/Памятник "Щас спою…"', ''),
('gen_random_uuid()', "0", 4, 'Бронзовый двухметровый памятник А. П. Чехову установлен 20 августа 2004 года в честь 400-летия Томска на набережной реки Томь недалеко от устья реки Ушайки. Автор памятника — скульптор Леонтий Усов, литейщик Максим Петров. Памятник создан на народные средства, одними из первых жертвователей стали: мэр города Александр Макаров, его заместитель Сергей Лазарев, управляющий томским филиалом Росбанка Чингис Акатаев, а также владелец ресторана «Славянский базар» Владимир Бурковский. Писатель изображён в гротескном и карикатурном виде: в пальто, нелепой шляпе, перекошенных очках, босиком с непропорционально большими ногами. За спиной у писателя зонт. Надпись на постаменте гласит: Антон Павлович в Томске глазами пьяного мужика, лежащего в канаве и не читавшего «Каштанку»', 0, 56.485356, 84.944925, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/chehov/', 'Памятник Чехову', ''),
('gen_random_uuid()', "0", 4, 'Но́во-Собо́рная — площадь в Томске. Образована проспектом Ленина, проездом к Спортивному переулку, Советской улицей и подъездом к городскому саду от проспекта Ленина. На момент основания Томска (1604) исторический район Подгорная (Нижняя) Елань, где позже появится площадь, представлял собой вековую целину, которую весной 1605 года вспахали и засеяли, превратив в «государеву пашню»', 0, 56.474, 84.951687, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/novosobornaya/', 'Новособорная площадь', ''),
('gen_random_uuid()', "0", 4, 'Усадьба купца второй гильдии Леонтия Желябо — одна из культовых достопримечательностей Красноармейской. Томский городской архитектор Петр Федоровский выдал разрешение на строительство бывшему унтер-офицеру в 1896 году. Постройка состоит из четырех зданий и хозяйственных объектов. По словам сотрудницы Томского областного художественного музея Людмилы Исаевой, дом с жар-птицами построен в 1903 году. Именно это здание из всего комплекса попало на эмблему к 400-летию Томска. Леонтий Желябо был зажиточным купцом, в его усадьбе было много прислуги. Простой контур здания обогащают пристроенные эркеры с двух сторон. Дом отличается лаконизмом целого и богатством отдельных декоративных элементов.  Дом после национализации всегда был жилым. С начала 2000-х жители жаловались на протекание крыши. Ситуацию поменяла реставрация 2008 года, ремонт крыши и косметический ремонт фасада. По словам сотрудницы Томского областного художественного музея Людмилы Исаевой, любопытная деталь – свитки на наличниках. Она подозревает, что это имеет отношение к тому, что Желябо был иудеем. Скорее всего, в свитках изображены Талмуд или Тора.', 3, 56.458859, 84.949559, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/DomWithZharPtiza/', 'Дом с жар-птицами', ''),
('gen_random_uuid()', "0", 4, 'Дом с шатром построили в 1904 году по заказу купца Георгия Голованова. По словам старшего научного сотрудника Омского областного художественного музея Людмилы Исаевой, купец овдовел, у него остались сыновья. Он женился вновь, и нужен был интересный новый дом для большой семьи. У купца было четверо сыновей: Вениамин, Сергей, Леонид и Александр. Проект выполнил известный в городе Станислав Хомич. Усадьба Голованова с садом занимала треть квартала, ограниченного улицами Солдатской, Ярлыковской, Торговой и Александровской (Красноармейской, Карташова, Вершинина, Герцена). Голованов родился в 1830 году в крестьянской семье. Первый магазин открыл в 1872 году. Со временем купец стал одним из богатейших людей Сибири. По данным личного дела, в ХХ веке Голованов уже числился купцом второй гильдии и имел собственный торговый дом «Голованов и сыновья». В 1910 году купца наградили званием потомственного почетного гражданина.', 3, 56.46977106823814, 84.96480650000002, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/DomWithShatrom/', 'Дом с шатром', ''),
('gen_random_uuid()', "0", 4, 'Памятник Н.П. Путинцеву. Памятник, изображающий инспектора ГАИ в полный рост, установлен на пересечении проспекта Ленина с переулком Нахановича – постоянном месте службы легендарного инспектора: последние 20 лет Николай Платонович нес службу на самом ответственном и напряженном участке в Томске - перекрестке проспекта Ленина и переулка Нахановича. Открыт 17 сентября 2012 г.', 0, 56.482854, 84.948716, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/DadyaKolya/', 'Дядя Коля, памятник инспектору ГАИ', ''),
('gen_random_uuid()', "0", 4, 'Жилой дом учителя Б.А. Быстрицкого был построен в 1917 году. Архитектура здания эффектна и несколько претенциозна. Контур здания усложнён, введена угловая башня, заканчивающаяся острым шатром, высокая кровля. Щипцы фронтонов увенчаны стилизованными гребнями-коньками — мотив, нашедший богатое воплощение в народном деревянном зодчестве, но здесь получивший совершенно новое модернистское решение - чисто декоративное, в отличие от народного, где эта форма являлась органической и конструктивной. Построен в 1917 г.', 0, 56.472272, 84.966002, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/DomWithDrakon/', 'Дом с драконами', ''),
('gen_random_uuid()', "0", 4, 'Памятник любовнику, или карикатура на любовника, как называет скульптуру ее автор, за 20 лет пережил много путешествий по разным домам Томска и обзавелся десятком новых трусов. Его история началась с эротической выставки, а продолжается на карнизе Музея истории Томска. По словам томского художника Олега Кислицкого, изначально идея создать подобный памятник появилась из практических соображений. Позже эта идея нашла свое воплощение, когда в нулевых годах готовилась первая в Сибири эротическая выставка "Э-рос-гильдяй". Тогда у трех "отцов"-художников и "родился" на свет любовник, который свисает с балкона в майке, трусах и с часами на руке. Любовник – некая монументальная карикатура в камне, не памятник, как настаивает Кислицкий: "Никто никогда не собирался делать его памятником. Тогда слова "инсталляция" еще не было. А вот в рамках этой выставки он очень хорошо себя проявил. Мы его открыли при духовом оркестре, все красиво было".', 1, 56.48874, 84.952724, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/PamatnikLovenik/', 'Памятник Любовнику', ''),
('gen_random_uuid()', "0", 4, 'Богородице́-Алексе́евский монасты́рь — православный мужской монастырь в Томске, находится в юрисдикции Томской епархии Русской православной церкви. Сведения о времени основании монастыря расходятся — по одним источникам он был основан в 1605, по другим — в 1622 году. Известно, что монастырь точно существовал в 1630-е годы. Столь раннее появление монастыря в только что основанном городе А. Латышев объясняет правительственными указами, а не большим количеством потенциальных насельников, мечтающих удалиться от мира. Первоначальное население Томска состояло из разгульных казаков, корыстолюбивых промышленников, иностранных искателей богатства, высланных поляков, сосланных каторжан, местных татар, «которых обычаи нравились русским, и многоженство нашло и подражателей». «Правительство видело, как необходимо в таком краю заведение благочестивое, подающее пример христианских добродетелей…» В 1630—1650-х годах Богородицкий монастырь находился у впадении реки Киргизки в Томь (Усть-Киргизский монастырь). В 1658 году перенесен на Юрточную гору. При строительстве были устроены подземные склады и ходы к реке Ушайке (на кирпичный свод наткнулись рабочие, производившие земляные работы в начале ноября 1888 года во дворе столоначальника томской казенной палаты В. Б. Орлова, жившего в конце тянувшейся от монастырской ограды к реке Ушайке улицы). В 1663 году освящена церковь во имя Св. Алексея, человека Божия, монастырь стал называться Богородице-Алексеевским. Из Богородице-Алексеевского монастыря осуществлялось руководство прочими восемью сибирскими монастырями, входившими в Томский разряд. Монастырь до 1764 года был единственным в Томской губернии владельцем 400 крепостных крестьян, а также обладал земельными угодьями на реках Томи и Оби. В XVII веке при монастыре была открыта больница, а в 1746 году первая томская школа — Томское русское духовное училище (в 1762 году преобразовано в русско-латинское). Также при монастыре с 1858 года действовала духовная семинария с обширной библиотекой. Монастырь, как и другие сибирские обители, был местом ссылки нарушителей монастырских уставов. Также в нём содержались и светские лица. Главным монастырским храмом является Казанская церковь, имеющая также приделы в честь Алексия, человека Божьего и святых Флора и Лавра. Изначально постройка была деревянная, неоднократно сгорала. В 1789 году было построено современное каменное церковное здание в стиле сибирского барокко. В церкви находился особо почитаемый образ Божией матери «Неопалимая купина». В 1830-е годы монастырь окружили каменными стенами и башнями-часовнями (проект томского архитектора К. Г. Турского). На территории находились сад, озеро, летние кельи и кладбище, а к югу от монастыря — монастырская роща, сохранявшаяся до начала XX века.', 0, 56.48181206826907, 84.954521, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/ManMonastyr/', 'Богородице-Алексиевский мужской монастырь', ''),
('gen_random_uuid()', "0", 4, 'Установлен в 2008 году на проспекте Ленина, 65, напротив входа в здание роддома №1. Скульптура малой формы «Младенец в капусте» представляет собой полураскрытый кочан капусты, из которого появляется на свет младенец. Выполнена из камня, диаметр кочана – 80 сантиметров, рост малыша – около 60 сантиметров. Скульптура установлена на гранитном постаменте, общая высота – около 1,5 м. Памятник помогает родителям отвечать на вечный вопрос сыновей и дочке, откуда берутся дети. Скульптор – Олег Кислицкий.', 3, 56.471493, 84.950804, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/PamatnikKapuste/', 'Памятник "Младенец в капусте"', ''),
('gen_random_uuid()', "0", 4, 'Воскресенская церковь — православный храм в Томске. По имени храма названа Воскресенская гора, на мысу которой он находится. Церковь построена в редком стиле сибирского барокко по типовому проекту Растрелли. К югу от церкви проходит Раскат (он же Октябрьский — бывший Воскресенский взвоз). Храм первоначально был деревянным и находился на территории Богородице-Успенского мужского монастыря, упразднённого в 1626 году. Её основатель — первый архиерей Тобольский Киприан в 1622 году. Обветшавший из-за частых пожаров храм решили перестроить. Так в 1789 году Егором Домоневским и Петром Барановым был заложен каменный фундамент церкви, однако её строительство часто предписывают томским мещанам Ивану Карпову и Ивану Недомолвину. 18 октября 1803 года освятили новую каменную Воскресенскую церковь. Престол в нижнем приделе был освящён в честь Обновления храма Воскресения Господня в Иерусалиме. Около четырёх лет шло обустройство верхнего этажа, и 16 июня 1807 года был освящён верхний престол храма — в честь Успения Божьей Матери. В конце XIX века холодный верхний этаж был перестроен в тёплый на средства прихожан храма П. Б. Шумилова и А. Е. Кухтерина. В 1897 году на отдельно стоящую звонницу был поднят отлитый по радению Андрея Афанасьевича Васильева колокол весом 16 тонн. В 1922 году по акту губернской комиссии изъятии церковных ценностей у храма забрали часть серебряной утвари — большие и малый кресты, подсвечники, кадило, лампады и т. д. В 1930 году церковный колокол был снят и пошёл на переплавку. 4 августа 1936 года храм закрыли, однако спустя 21 день её вновь открыли по просьбе прихожан, 3 ноября вновь закрыт. В 1937 году была предпринята попытка сноса церкви, но была снесена лишь церковная ограда. Госстройтрест (курсы и общежитие рабочих), гараж для машин, а затем склад для зерна находились в здании храма в 1930-е годы. В 1940-е года в храм из-за боязни вторжения японских войск в СССР были перенесены архивы РСФСР по Дальнему Востоку.В 1980—1990 годах прошла реставрация фасадов здания, были отремонтированы алтари, восстановлен каркас иконостаса храма, его купола и крыша, восстановлены позолоченные кресты. В 2004 году копия знаменитого колокола была поднята на вновь выстроенную звонницу.', 1, 56.493919068240125, 84.95464649999995, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/VoskresenskayaCerkov/', 'Воскресенская церковь', ''),
('gen_random_uuid()', "0", 4, 'Богоявленский собор — кафедральный собор Томской митрополии Русской Православной Церкви. Расположен в городе Томске. Богоявленский собор является одним из древнейших томских храмов. Он берёт своё начало от деревянной церкви, построенной в 1630 году[1]. Позже на месте алтаря старой деревянной церкви в 1858 году построили часовню Иверской иконы Божией Матери. Существующее каменное двухэтажное здание собора в стиле барокко было заложено в 1777 году. Нижний этаж каменной церкви был освящён в 1784 году в честь Богоявления Господня. Вскоре на нижнем этаже был освящён придел Архангела Михаила, а на верхнем — Ильинский и Спасский приделы. Храм имеет особое значение для томской истории. Именно Богоявленскую церковь выбрал первый томский губернатор В. С. Хвостов, в которой на праздник Преображения Господня 6 августа 1804 году было объявлено открытие Томской губернии. С 1892 года при соборе существовало попечительство о бедных, с 1898 года — церковно-приходская школа. При храме работала библиотека, насчитывавшая 400 томов книг. Поскольку храм был посвящён Богоявлению или Крещению Господня, то ежегодно 19 января (по старому стилю 6 января) на Крещение из храма устраивался крестной ход на реку Томь для совершения обряда Великого водоосвящения. Крестный ход устраивался также в день Пятидесятницы и в праздник Первому Спасу. Собор для богослужения закрыли в 1930 год и открыли в нём курсы Сибжелдорстроя. В 1936 году в здании разместили вермишелевую фабрику, а в 1942 году сюда был эвакуирован завод «Красный богатырь». С 1947 по 1994 года здесь размещался Томский завод резиновой обуви. Во второй половине XX века храм сильно пострадал. Были сняты все купола и кресты, разрушен восьмерик завершения колокольни, пристроены дополнительные сооружения, а на южном фасаде срубили декор и изменили форму оконных проёмов.', 2, 56.48858656825646, 84.94728949999994, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/KafedrSobor/', 'Богоявленский Кафедральный собор', ''),
('gen_random_uuid()', "0", 4, 'В 2005 году на проспекте Ленина д. 38, напротив входа в акушерские клиники Сибирского государственного медицинского университета, появился памятник беременной женщине. Автор — томский скульптор Николай Гнедых. Памятник установлен в честь 115-й годовщины со дня открытия акушерских клиник. Скульптура авангардная: тело матери сделано из железных каркасов, сквозь которые виден её будущий ребёнок. Для пущей реалистичности образа скульптор прибег к рекомендациям врачей-гинекологов, что позволяет увидеть, как располагается плод на поздних сроках беременности. Скульптура породила городскую примету: если беременная женщина погладит статую по животу или повяжет ленточку на подол платья, роды у неё пройдут благополучно.', 3, 56.47234, 84.950107, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/pamatnikBeremZhenshine/', 'Памятник беременной женщине', ''),
('gen_random_uuid()', "0", 4, 'Самый маленький памятник в мире был открыт в Томске 27 сентября 2013 года. Бронзовый памятник земноводному высотой всего 44 миллиметра. Автор мини-скульптуры - Олег Кислицкий, решил таким образом отметить заслуги томских путешественников. Композиция представляет собой обложенный плиткой участок земли, в центре располагается камень, на котором в задумчивости сидит смотрящая в небо лягушка. Памятник снабдили сопроводительной надписью: "Памятник лягушке-путешественнице, самый маленький памятник в мире". ', 3, 56.460782, 84.988739, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/pamatnikLagushke/', 'Памятник лягушке-путешественнице', ''),
('gen_random_uuid()', "0", 4, 'Томск является красивым российским городом с интересной 400-летней историей. С каждым годом на городской территории появляются новые здания и достопримечательные объекты, благодаря которым Томск становится более современным и привлекательным для туристов мегаполисом. Одним из них является памятник 400 лет Томску (Вековые столпы 400-летия Томска). Этот величественный монумент, который был установлен в честь празднования 400-летия города, установлен на городской набережной. Памятник 400 лет Томску (Вековые столпы 400-летия Томска) является частью объектов мемориальной исторической зоны, которую соорудили специально в честь памятной для города даты. На вековых столпах 400-летия Томска можно рассмотреть наиболее важные этапы развития, а также становления Томска и Томской губернии. На них созданы рисунки и надписи, которые остаются в памяти прогуливавшихся местных жителей и туристов. Томские вековые столпы являются мраморными пирамидальными памятниками с установленными на них мемориальными досками. Таких столпов здесь всего четыре. Каждый из мраморных памятников повествует об эпохальных временах XVII – XX веков в истории края. Авторами проекта стали местные мастера Н.П. и А.Н. Гнедых.', 2, 56.486907, 84.946256, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/pamatnik400Years/', 'Памятник 400 лет Томску', ''),
('gen_random_uuid()', "0", 4, 'Кра́сная мече́ть (Первая соборная мечеть Томска) — каменная мечеть в татарской слободе Томска. Первые томские мечети были деревянными, первая из них была построена в 1810 году, затем сгорела. На её месте, на углу улицы Татарской и переулка Татарского (ныне улица Трифонова), была построена вторая деревянная мечеть, которая также сгорела в 1900 году. Современная мечеть была построена в 1901—1904 годах ахуном Хамзой Хамитовым на средства купца Карима Хамитова. В 1907 году рядом со зданием мечети было построено медресе. В народе оно было известно как Хамитовское. В 1931 году мечеть была закрыта, здание использовалось как кинозал, ликёро-водочный завод, цех завода пищевых продуктов «Томский». В 1995—1997 годах мусульманской общине вернули здание медресе. Второй этаж этого здания используется в качестве молельни. Само здание мечети было возвращено верующим только в 2002 году в полуразрушенном состоянии. В 2014 году на средства представителей Чеченской Республики активно восстанавливается, возводятся утраченные минарет и купол. В декабре 2014 года была завершена реставрация Красной мечети[2]. В марте 2015 года состоялась церемония открытия реконструированного здания. На открытии присутствовали делегации Чеченской Республики, ДУМ Азиатской части России, представители органов власти Томской области и города', 0, 56.47849656823048, 84.945376, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/RedSoborMechet/', 'Красная соборная мечеть', ''),
('gen_random_uuid()', "0", 4, 'Александр Сергеевич Пушкин (1799—1837) — русский поэт, драматург и прозаик, заложивший основы русского реалистического направления, литературовед, историк, публицист; один из самых авторитетных литературных деятелей первой трети XIX века. Ещё при жизни Пушкина сложилась его репутация величайшего национального русского поэта. Пушкин рассматривается как основоположник современного русского литературного языка. Трагически погиб в результате дуэли, которая состоялась состоялась 27 января 1837 года в пригороде Санкт-Петербурга. Перед дуэлью имел планы со своими товарищами по поездке в Сибирь, где надеялся встретиться с ссыльными декабристами. Томская общественность уже в конце XIX века решила увековечить в губернской столице память великого поэта А.С. Пушкина. В 1898 году на пространстве от Бесплатной народной библиотеки до пароходной пристани на Томи город заложил прекрасный парк, названный с 1899 года Пушкинским. Ныне это территория площади 1812 года перед Речным вокзалом. Также было решено на деньги сообщества городских граждан установить памятник А.С. Пушкину в центре нового сквера. Однако собранных денег не хватило и памятник появился лишь в 1915 году. В период после Гражданской войны памятник мирно стоял в парке, который быстро зарос и некоторое время до него не было внимания властей. В 1920-е годы Бесплатная библиотека была закрыта а её книги и различные исторические артефакты, собранные в народном музее при Библиотеке, были спешно вывезены в направлении новой пролетарской столицы Сибири, то есть оказались безвозвратно утеряны. Место перестало иметь какой-либо поэтический или любой литературный смысл. Второй памятник-бюст А.С. Пушкину был установлен в 1954 году в Городском саду. Однако он был изготовлен из гипса, который вскоре начал разрушаться и в 1962 году бюст был демонтирован. Третий памятник А.С. Пушкину был установлен самоинициативно коллективом НИИ кабельной продукции (НИКИ) ориентировочно в 1963 году у своего здания по улице Пушкина, 44. Отлитый из железобетона, небольшой по размерам, весьма симпатичный памятник изображал поэта, сидящего на скамейке. Он напоминал известный памятник Пушкину-лицеисту в 1900 году в Царском Селе работы зодчего Роберта Баха. Этот памятник простоял всего несколько лет и был утрачен около 1968 года. Тогда горисполком принял решение о переносе памятника в Детский парк Дома пионеров, рабочие попытались снять монумент с пьедестала, но бетон разрушился и памятник оказался уничтожен. Обломки на тросе за трактором были оттащены в ров Каштачного оврага где-то у улицы Дальне-Ключевской. Одним из самых художественно-привлекательных в Томске каменных изображений поэта является бюст поэта работы скульптора Е.Ф. Белашовой. Первоначально в 1954 году мастер изготовила в Москве своё произведение из тонированного гипса, а чуть позднее для Томска сделала повторение этого бюста в мраморе. В настоящее время он украшает холл на втором этаже областной Пушкинской библиотеки.', 0, 56.477286, 84.950398, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/PamatnikASPushkin/', 'Памятник А.С.Пушкину', ''),
('gen_random_uuid()', "0", 4, 'Памятник домашним тапочкам - это скульптурная композиция, которая находится у входа в гостиницу «Томск». Монумент был создан в 2006 году, и представляет собой бронзовые домашние тапочки, установленные на 20-и сантиметровом постаменте. Длина необычных тапочек составляет 32 сантиметра, поэтому померить их может каждый желающий, независимо от размера ноги. На постаменте расположена надпись: «Будьте как дома». Как известно, пямятник символизирует уют и гостеприимство города Томска. Автором забавного проекта является томский скульптор Олег Кислицский. Идея создания такого монумента пришла автора при выходе из гостиницы. И в 2006 году композиция памятника была предложена к воплощению на форуме отельеров, проводимом в Томске. Проект был одобрен и реализован в том же году. При этом скульптор отмечает, что памятник символизирует именно комнатные, стоптанные и ношеные тапочки, которыми мы пользуемся каждый день. Ведь именно домашняя обстановка ассоциируется у людей с уютом. Изначально планировалось сделать пару бронзовых помпончиков, однако идея была отвергнута. И теперь каждый турист и местный житель может примерить бронзовые "теплые", "мягкие"  тапочки и ощутить по истине домашний уют.', 3, 56.46096, 84.988857, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/PamatnikHomeTapochki/', 'Памятник домашним тапочкам', ''),
('gen_random_uuid()', "0", 4, 'Накануне в Томске у гостиницы "Томск" была установлена скульптурная композиция "Три медведя". Деревянный памятник трем медведям был установлен в пятницу, 30 мая. Скульптурная композиция представляет из себя беседку, под которой укрылись 3 медведя: один постарше в кепке а-ля Лужков, медведица и медвежонок с транспарантом. Взрослые медведи держат в руке по лампе. На крыше беседки сидит сова в очках, смотрящая вниз на проходящих мимо. Автор скульптурной композиции председатель Томского союза резчиков по дереву Владимир Рубин. ', 3, 56.460996, 84.989039, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/Skulptura3Medvedya/', 'Скульптурная композиция "Три медведя"', ''),
('gen_random_uuid()', "0", 4, 'Памятник героям сказки «Волшебник Изумрудного города» находится на проспекте Комсомольский возле Торгового центра «Изумрудный город». Октябрьский район города Томска. Писатель и профессиональный переводчик, педагог Александр Мелентьевич Волков (он в 1910 году окончил Томский учительский институт, правопреемником которого ныне считается Педагогический университет) в 1939 году на основе сказки Фрэнка Баума «Удивительный волшебник из страны Оз» (англ. The Wonderful Wizard of Oz) с рядом изменений и литературных улучшений создал русский вариант сказки, которую он назвал «Волшебник Изумрудного города». Сказка в СССР получила невероятную популярность, её с благодарностью приняла восторженная публика. В 1959 году вышло новое издание книги, значительно переработанное автором. В этом издании впервые появились иллюстрации художника Л.В. Владимирского. Впоследствии книга была переработана ещё раз. И, если у Баума была всего одна книга, то у Волкова после этой, первой, вышло ещё 5 книг, связанных единой сюжетной линией. Только первая из книг прямо отталкивается от оригинального варианта книги «Удивительный волшебник из страны Оз», не повторяя его буквально. За неимением специального, общего для всего цикла имени, «Волшебником Изумрудного города» называют и весь цикл книг А.М. Волкова. Благодаря сотрудничеству художника Мариана Монцмана (Словакия, город Братислава), томского литератора Андрея Олеара (автор идеи памятника) и скульптора Мартина Пала на свет появился памятник героям знаменитой сказки Александра Волкова «Волшебник Изумрудного города», открытие которого состоялось сегодня, 31 мая, в Томске.', 1, 56.487792, 84.980156, '2022-10-13 13:23:29.086377+07', 'Tomsk', '~/img/Db/img/Entertainment/PamatnikIzumrCity/', 'Памятник героям сказки "Волшебник изумрудного города"', ''),